// Frame sizes of the vol array
uint16_t vol_frame_sizes[4] = {sizeof(epd_bitmap_Vol0), sizeof(epd_bitmap_Vol1), sizeof(epd_bitmap_Vol2), sizeof(epd_bitmap_Vol3)};

// Frame sizes of the sun array
uint16_t sun_frame_sizes[4] = {sizeof(epd_bitmap_Sun0), sizeof(epd_bitmap_Sun1), sizeof(epd_bitmap_Sun2), sizeof(epd_bitmap_Sun3)};

// OLED Function
#ifdef OLED_ENABLE

static void render_animation(void) {
    uint8_t current_layer = get_highest_layer(layer_state);

    // Check if layer changed during animation
    if (current_layer != prev_layer) {
        // Reset animation state for new layer
        frame_index = 3;
        animation_in_progress = false;
        prev_layer = current_layer;
    }    

    // Run Animation
    if (IS_LAYER_ON(_BL)) {
        oled_write_raw_P(epd_bitmap_allVolArray[frame_index], vol_frame_sizes[frame_index]);
    } else if (IS_LAYER_ON(_SL)) {
        oled_write_raw_P(epd_bitmap_allSunArray[frame_index], sun_frame_sizes[frame_index]);
    }

    // Update the frame index based on timing
    if (timer_elapsed(animation_timer) > ANIMATION_FRAME_DELAY && animation_in_progress) {
        animation_timer = timer_read();
        
        if (encoder_state == 1) { // Forward animation
            frame_index++;
            if (frame_index >= 4) {
                animation_in_progress = false;
            }
        } else if (encoder_state == -1) { // Backward animation
            if (frame_index == 0) {
                animation_in_progress = false;
            } else {
                frame_index--;
            }
        }
    }
}

bool oled_task_user(void) {
    static bool oled_initialized = false;
    uint8_t current_layer = get_highest_layer(layer_state);

    // Initialize OLED display once
    if (!oled_initialized) {
        oled_clear();
        prev_layer = current_layer;
        oled_initialized = true;
    }

    // Display the layer info at the top
    oled_set_cursor(0, 0);
    switch (get_highest_layer(layer_state)) {
        case _BL:
            oled_write_ln_P(PSTR("Layer: Ctrl"), false);
            break;
        case _SL:
            oled_write_ln_P(PSTR("Layer: Func"), false);
            break;
        default:
            oled_write_ln_P(PSTR("Layer: Undefined"), false);
            break;
    }

    // Display encoder functionality
    oled_set_cursor(0, 1);
    if (IS_LAYER_ON(_BL)) {
        oled_write_ln_P(PSTR("Vol Ctrl"), false);
    } else if (IS_LAYER_ON(_SL)) {
        oled_write_ln_P(PSTR("Bright Ctrl"), false);
    }

    // Handle layer change even when not animating
    if (current_layer != prev_layer && !animation_in_progress) {
        prev_layer = current_layer;
    }

    // Render the animation below the layer info
    if (animation_in_progress) {
        render_animation();
    } else {
        // Display static image based on current layer
        if (IS_LAYER_ON(_BL)) {
            oled_write_raw_P(epd_bitmap_allVolArray[3], vol_frame_sizes[3]);
        } else if (IS_LAYER_ON(_SL)) {
            oled_write_raw_P(epd_bitmap_allSunArray[3], sun_frame_sizes[3]);
        }
    }

    return false;
}

#endif // OLED_ENABLE
